	reference_module good1 (
		.y,
		.w,
		.Y1(Y1_ref),
		.Y3(Y3_ref) );

	top_module top_module1 (
		.y,
		.w,
		.Y1(Y1_dut),
		.Y3(Y3_dut) );


	bit strobe = 0;
