detail_description:
// Consider the state machine shown below:

// A (0) --1--> B
// A (0) --0--> A
// B (0) --1--> C
// B (0) --0--> D
// C (0) --1--> E
// C (0) --0--> D
// D (0) --1--> F
// D (0) --0--> A
// E (1) --1--> E
// E (1) --0--> D
// F (1) --1--> C
// F (1) --0--> D

// Assume that a one-hot code is used with the state assignment y[5:0] = 000001(A), 000010(B), 000100(C), 001000(D), 010000(E), 100000(F)

// Write a Verilog for the signal Y1, which is the input of state flip-flop y[1], for the signal Y3, which is the input of state flip-flop y[3]. Derive the Verilog by inspection assuming a one-hot encoding.

solution:
module top_module (
input clk,
input reset,
input w,
output reg z
);
parameter A = 3'b000;
parameter B = 3'b001;
parameter C = 3'b010;
parameter D = 3'b011;
parameter E = 3'b100;
parameter F = 3'b101;
reg [2:0] current_state;
reg [2:0] next_state;
always @(posedge clk or posedge reset) begin
if (reset) begin
current_state <= A;
end else begin
current_state <= next_state;
end
end
always @* begin
case (current_state)
A: begin
if (w) begin
next_state = B;
end else begin
next_state = A;
end
end
B: begin
if (w) begin
next_state = C;
end else begin
next_state = D;
end
end
C: begin
if (w) begin
next_state = E;
end else begin
next_state = D;
end
end
D: begin
if (w) begin
next_state = F;
end else begin
next_state = A;
end
end
E: begin
if (w) begin
next_state = E;
end else begin
next_state = D;
end
end
F: begin
if (w) begin
next_state = C;
end else begin
next_state = D;
end
end
endcase
end
always @(current_state) begin
case (current_state)
A: begin
z <= 1'b0;
end
B: begin
z <= 1'b0;
end
C: begin
z <= 1'b0;
end
D: begin
z <= 1'b0;
end
E: begin
z <= 1'b1;
end
F: begin
z <= 1'b1;
end
endcase
end
endmodule

