solution:
module top_module (
input clk,
input reset,
input w,
output reg z
);
reg [2:0] state, next_state;
always @(posedge clk or posedge reset) begin
if (reset)
state <= 3'b000;
else
state <= next_state;
end
always @(state or w) begin
case (state)
3'b000: if (w) next_state = 3'b001; else next_state = 3'b000;
3'b001: if (w) next_state = 3'b010; else next_state = 3'b011;
3'b010: if (w) next_state = 3'b100; else next_state = 3'b011;
3'b011: if (w) next_state = 3'b101; else next_state = 3'b000;
3'b100: if (w) next_state = 3'b101; else next_state = 3'b011;
3'b101: if (w) next_state = 3'b101; else next_state = 3'b011;
default: next_state = 3'b000;
endcase
end
always @(state) begin
case (state)
3'b000: z = 1'b0;
3'b001: z = 1'b0;
3'b010: z = 1'b0;
3'b011: z = 1'b0;
3'b100: z = 1'b0;
3'b101: z = 1'b1;
default: z = 1'b0;
endcase
end
