solution:
module top_module (
input clk,
input resetn,
input x,
input y,
output reg f,
output reg g
);
parameter A = 0;
parameter B = 1;
parameter S0 = 2;
parameter S1 = 3;
parameter S10 = 4;
parameter G1 = 5;
parameter G2 = 6;
parameter P0 = 7;
parameter P1 = 8;
reg [3:0] state;
reg [3:0] next_state;
always @(posedge clk or negedge resetn) begin
if (!resetn) begin
state <= A;
end else begin
state <= next_state;
end
end
always @(state, x, y) begin
case (state)
A: begin
if (x) begin
next_state = B;
end else begin
next_state = A;
end
end
B: begin
next_state = S0;
end
S0: begin
if (x) begin
next_state = S1;
end else begin
next_state = S0;
end
end
S1: begin
if (x) begin
next_state = S1;
end else begin
next_state = S10;
end
end
S10: begin
if (x) begin
next_state = G1;
end else begin
next_state = S0;
end
end
G1: begin
if (y) begin
next_state = P1;
end else begin
next_state = G2;
end
end
G2: begin
if (y) begin
next_state = P1;
end else begin
next_state = P0;
end
end
P0: begin
next_state = P0;
end
P1: begin
next_state = P1;
end
endcase
end
always @(state) begin
case (state)
B: begin
f = 1;
end
G1, G2, P1: begin
g = 1;
end
default: begin
g = 0;
end
endcase
end
endmodule

