// 011 | 001, 010 | 1
// 100 | 011, 100 | 1

prompt:
module top_module (
	input clk,
	input reset,
	input x,
	output reg z
);

canonical_solution:
	parameter A=0, B=1, C=2, D=3, E=4;
	reg [2:0] state, next;

	always @(posedge clk) begin
		if (reset) state <= A;
		else state <= next;
