module top_module(
input clk,
input reset,
input ena,
output reg pm,
output reg [7:0] hh,
output reg [7:0] mm,
output reg [7:0] ss);
reg [3:0] hour_tens;
reg [3:0] hour_ones;
reg [5:0] minute_tens;
reg [3:0] minute_ones;
reg [5:0] second_tens;
reg [3:0] second_ones;
always @(posedge clk) begin
if (reset) begin
hour_tens <= 4'b0000;
hour_ones <= 4'b0000;
minute_tens <= 6'b000000;
minute_ones <= 4'b0000;
second_tens <= 6'b000000;
second_ones <= 4'b0000;
pm <= 1'b0;
end else if (ena) begin
if (second_ones == 4'b1001) begin
second_ones <= 4'b0000;
if (second_tens == 6'b010111) begin
second_tens <= 6'b000000;
if (minute_ones == 4'b1001) begin
minute_ones <= 4'b0000;
if (minute_tens == 6'b010111) begin
minute_tens <= 6'b000000;
if (hour_ones == 4'b1001) begin
hour_ones <= 4'b0000;
if (hour_tens == 4'b0101) begin
hour_tens <= 4'b0000;
pm <= ~pm;
end else begin
hour_tens <= hour_tens + 1;
end
end else begin
hour_ones <= hour_ones + 1;
end
end else begin
minute_tens <= minute_tens + 1;
end
end else begin
minute_ones <= minute_ones + 1;
end
end else begin
second_tens <= second_tens + 1;
end
end else begin
second_ones <= second_ones + 1;
end
end
end
assign hh = {hour_tens, hour_ones};
assign mm = {minute_tens, minute_ones};
assign ss = {second_tens, second_ones};
endmodule

