module top_module(
input clk,
input reset,
input ena,
output reg pm,
output reg [7:0] hh,
output reg [7:0] mm,
output reg [7:0] ss);
reg [3:0] hour_tens;
reg [3:0] hour_ones;
reg [5:0] minute_tens;
reg [3:0] minute_ones;
reg [5:0] second_tens;
reg [3:0] second_ones;
always @(posedge clk or posedge reset) begin
if (reset) begin
hour_tens <= 4'b0000;
hour_ones <= 4'b0000;
end else if (ena) begin
if (hour_tens == 4'b0000 && hour_ones == 4'b1010) begin
hour_tens <= 4'b0001;
hour_ones <= 4'b0000;
end else if (hour_ones == 4'b1001) begin
hour_tens <= hour_tens + 1;
hour_ones <= 4'b0000;
end else begin
hour_ones <= hour_ones + 1;
end
end
end
always @(posedge clk or posedge reset) begin
if (reset) begin
minute_tens <= 6'b000000;
minute_ones <= 4'b0000;
end else if (ena) begin
if (minute_tens == 6'b000011 && minute_ones == 4'b1001) begin
minute_tens <= 6'b000000;
minute_ones <= 4'b0000;
end else if (minute_ones == 4'b1001) begin
minute_tens <= minute_tens + 1;
minute_ones <= 4'b0000;
end else begin
minute_ones <= minute_ones + 1;
end
end
end
always @(posedge clk or posedge reset) begin
if (reset) begin
second_tens <= 6'b000000;
second_ones <= 4'b0000;
end else if (ena) begin
if (second_tens == 6'b000011 && second_ones == 4'b1001) begin
second_tens <= 6'b000000;
second_ones <= 4'b0000;
end else if (second_ones == 4'b1001) begin
second_tens <= second_tens + 1;
second_ones <= 4'b0000;
end else begin
second_ones <= second_ones + 1;
end
end
end
always @(posedge clk) begin
hh = {hour_tens, hour_ones};
mm = {minute_tens, minute_ones};
ss = {second_tens, second_ones};
pm = (hour_tens >= 4'b1010);
end
endmodule

