module top_module(
input clk,
input areset,
input train_valid,
input train_taken,
output logic [1:0] state
);
localparam STATE_0 = 2'b00;
localparam STATE_1 = 2'b01;
localparam STATE_2 = 2'b10;
localparam STATE_3 = 2'b11;
reg [1:0] state_reg;
reg [1:0] next_state;
always @(posedge clk or posedge areset) begin
if (areset) begin
state_reg <= STATE_1;
end else begin
state_reg <= next_state;
end
end
always @(state_reg, train_valid, train_taken) begin
case (state_reg)
STATE_0: begin
if (train_valid) begin
if (train_taken) begin
next_state = STATE_1;
end else begin
next_state = STATE_0;
end
end else begin
next_state = STATE_0;
end
end
STATE_1: begin
if (train_valid) begin
if (train_taken) begin
next_state = STATE_2;
end else begin
next_state = STATE_0;
end
end else begin
next_state = STATE_1;
end
end
STATE_2: begin
if (train_valid) begin
if (train_taken) begin
next_state = STATE_3;
end else begin
next_state = STATE_1;
end
end else begin
next_state = STATE_2;
end
end
STATE_3: begin
if (train_valid) begin
if (train_taken) begin
next_state = STATE_3;
end else begin
next_state = STATE_2;
end
end else begin
next_state = STATE_3;
end
end
endcase
end
assign state = state_reg;
endmodule
endmodule

