input clk,
input reset,
input [3:1] s,
output reg fr3,
output reg fr2,
output reg fr1,
output reg dfr
);
reg [3:1] s_reg;
reg [3:1] s_prev;
always @(posedge clk) begin
if (reset) begin
s_reg <= 3'b000;
s_prev <= 3'b000;
fr3 <= 1'b1;
fr2 <= 1'b1;
fr1 <= 1'b1;
dfr <= 1'b1;
end else begin
s_reg <= s;
s_prev <= s_reg;
case (s_reg)
3'b111: begin
fr3 <= 1'b0;
fr2 <= 1'b0;
fr1 <= 1'b0;
dfr <= 1'b0;
end
3'b110, 3'b101: begin
fr3 <= 1'b0;
fr2 <= 1'b0;
fr1 <= 1'b1;
dfr <= 1'b0;
end
3'b100: begin
fr3 <= 1'b0;
fr2 <= 1'b1;
fr1 <= 1'b1;
dfr <= 1'b0;
end
default: begin
fr3 <= 1'b1;
fr2 <= 1'b1;
fr1 <= 1'b1;
dfr <= 1'b1;
end
endcase
end
end
endmodule

