solution:
reg [1:0] state, next_state;
always @(posedge clk or negedge aresetn) begin
if (~aresetn) begin
state <= 2'b00;
end else begin
state <= next_state;
end
end
always @(state or x) begin
case (state)
2'b00: begin
if (x == 1'b0) begin
next_state = 2'b00;
end else begin
next_state = 2'b01;
end
end
2'b01: begin
if (x == 1'b0) begin
next_state = 2'b10;
end else begin
next_state = 2'b01;
end
end
2'b10: begin
if (x == 1'b0) begin
next_state = 2'b00;
end else begin
next_state = 2'b01;
end
end
default: next_state = 2'b00;
endcase
end
always @(state) begin
case (state)
2'b00, 2'b01: z = 1'b0;
2'b10: z = x;
default: z = 1'b0;
endcase
end
endmodule

