module top_module (
input clk,
input areset,
input x,
output reg z
);
reg [2:0] state;
parameter S0 = 3'b000;
parameter S1 = 3'b001;
parameter S2 = 3'b010;
parameter S3 = 3'b011;
parameter S4 = 3'b100;
always @(posedge clk or negedge areset) begin
if (areset == 0) begin
state <= S0;
end else begin
case (state)
S0: begin
if (x == 1'b1) begin
state <= S1;
end else begin
state <= S0;
end
end
S1: begin
if (x == 1'b1) begin
state <= S2;
end else begin
state <= S1;
end
end
S2: begin
if (x == 1'b1) begin
state <= S3;
end else begin
state <= S2;
end
end
S3: begin
if (x == 1'b1) begin
state <= S4;
end else begin
state <= S3;
end
end
S4: begin
if (x == 1'b1) begin
state <= S4;
end else begin
state <= S0;
end
end
endcase
end
end
always @(state) begin
case (state)
S0: begin
z <= 1'b0;
end
S1: begin
z <= 1'b1;
end
S2: begin
z <= 1'b0;
end
S3: begin
z <= 1'b1;
end
S4: begin
z <= 1'b0;
end
endcase
end
endmodule

