module top_module(
input clk,
input [7:0] in,
output reg [7:0] pedge);
always @(posedge clk) begin
for (int i=0; i<7; i=i+1) begin
if (in[i] == 0 && in[i+1] == 1)
pedge[i+1] <= 1;
else if (in[i] == 1 && in[i+1] == 0)
pedge[i+1] <= 0;
end
end
endmodule

