detail_description:
The following is the state transition table for a Moore state machine with one input, one output, and four states. Implement this state machine. Include a synchronous active high reset that resets the FSM to state A. 
// State | Next state in=0, Next state in=1 | Output
// A | A, B | 0
// B | C, B | 0
// C | A, D | 0
// D | C, B | 1

solution:
module top_module (
input clk,
input in,
input reset,
output out
);
parameter A = 2'b00;
parameter B = 2'b01;
parameter C = 2'b10;
parameter D = 2'b11;
reg [1:0] state_reg;
reg [1:0] next_state;
reg out_reg;
always @(posedge clk or posedge reset) begin
if (reset) begin
state_reg <= A;
end else begin
case (state_reg)
A: begin
if (in) begin
next_state <= B;
end else begin
next_state <= A;
end
end
B: begin
if (in) begin
next_state <= B;
end else begin
next_state <= C;
end
end
C: begin
if (in) begin
next_state <= D;
end else begin
next_state <= A;
end
end
D: begin
if (in) begin
next_state <= B;
end else begin
next_state <= C;
end
end
endcase
end
end
always @(posedge clk) begin
case (state_reg)
D: begin
out_reg <= 1;
end
default: begin
out_reg <= 0;
end
endcase
end
always @(posedge clk or posedge reset) begin
if (reset) begin
state_reg <= A;
end else begin
state_reg <= next_state;
end
end
assign out = out_reg;
endmodule

