module top_module (
input clk,
input reset,
input in,
output disc,
output flag,
output err
);
reg [2:0] state, next_state;
reg disc_reg, flag_reg, err_reg;
always @(posedge clk or posedge reset) begin
if (reset) begin
state <= 3'b000;
disc_reg <= 1'b0;
flag_reg <= 1'b0;
err_reg <= 1'b0;
end else begin
state <= next_state;
disc_reg <= 1'b0;
flag_reg <= 1'b0;
err_reg <= 1'b0;
end
end
always @(state, in) begin
case (state)
3'b000: begin // Initial state
if (in) begin
next_state = 3'b001;
end else begin
next_state = 3'b000;
end
end
3'b001: begin // State after seeing 0
if (in) begin
next_state = 3'b010;
end else begin
next_state = 3'b000;
end
end
3'b010: begin // State after seeing 01
if (in) begin
next_state = 3'b011;
end else begin
next_state = 3'b000;
end
end
3'b011: begin // State after seeing 011
if (in) begin
next_state = 3'b100;
end else begin
next_state = 3'b000;
end
end
3'b100: begin // State after seeing 0111
if (in) begin
next_state = 3'b101;
end else begin
next_state = 3'b000;
end
end
3'b101: begin // State after seeing 01111
if (in) begin
next_state = 3'b110;
end else begin
next_state = 3'b000;
end
end
3'b110: begin // State after seeing 011111
if (in) begin
next_state = 3'b111;
end else begin
next_state = 3'b000;
end
end
3'b111: begin // State after seeing 0111111
if (in) begin
next_state = 3'b111;
end else begin
next_state = 3'b000;
end
end
endcase
end
always @(state) begin
case (state)
3'b000: begin // Initial state
disc_reg = 1'b0;
flag_reg = 1'b0;
err_reg = 1'b0;
end
3'b001: begin // State after seeing 0
disc_reg = 1'b0;
flag_reg = 1'b0;
err_reg = 1'b0;
end
3'b010: begin // State after seeing 01
disc_reg = 1'b0;
flag_reg = 1'b0;
err_reg = 1'b0;
end
3'b011: begin // State after seeing 011
disc_reg = 1'b0;
flag_reg = 1'b0;
err_reg = 1'b0;
end
3'b100: begin // State after seeing 0111
disc_reg = 1'b0;
flag_reg = 1'b0;
err_reg = 1'b0;
end
3'b101: begin // State after seeing 01111
disc_reg = 1'b0;
flag_reg = 1'b0;
err_reg = 1'b0;
end
3'b110: begin // State after seeing 011111
disc_reg = 1'b0;
flag_reg = 1'b1;
err_reg = 1'b0;
end
3'b111: begin // State after seeing 0111111
disc_reg = 1'b1;
flag_reg = 1'b0;
