module top_module (
input clk,
input [7:0] in,
input reset,
output [23:0] out_bytes,
output done
);
parameter BYTE1 = 2'b00;
parameter BYTE2 = 2'b01;
parameter BYTE3 = 2'b10;
parameter DONE = 2'b11;
reg [1:0] state_reg;
reg [23:0] out_bytes_reg;
always @(posedge clk or posedge reset) begin
if (reset) begin
state_reg <= BYTE1;
end else begin
case (state_reg)
BYTE1: begin
if (in[2] == 1'b1) begin
state_reg <= BYTE2;
end else begin
state_reg <= BYTE1;
end
end
BYTE2: begin
state_reg <= BYTE3;
end
BYTE3: begin
state_reg <= DONE;
end
DONE: begin
if (in[2] == 1'b0) begin
state_reg <= BYTE1;
end else begin
state_reg <= DONE;
end
end
endcase
end
end
always @(posedge clk) begin
if (state_reg == DONE) begin
out_bytes_reg <= out_bytes_reg << 8;
out_bytes_reg[7:0] <= in;
end
end
assign out_bytes = out_bytes_reg;
assign done = (state_reg == DONE);
endmodule

