module top_module (
input clk,
input [7:0] in,
input reset,
output [23:0] out_bytes,
output done
);
parameter BYTE1 = 2'b00;
parameter BYTE2 = 2'b01;
parameter BYTE3 = 2'b10;
parameter DONE = 2'b11;
reg [1:0] state;
always @(posedge clk or posedge reset) begin
if (reset) begin
state <= BYTE1;
end else begin
case (state)
BYTE1: begin
if (in[2] == 1'b1) begin
state <= BYTE2;
end else begin
state <= BYTE1;
end
end
BYTE2: begin
state <= BYTE3;
end
BYTE3: begin
state <= DONE;
end
DONE: begin
if (in[2] == 1'b0) begin
state <= BYTE1;
end else begin
state <= DONE;
end
end
endcase
end
end
reg [23:0] out_reg;
always @(posedge clk) begin
if (reset) begin
out_reg <= 24'b0;
end else begin
if (state == DONE) begin
out_reg <= out_reg;
end else begin
out_reg <= {out_reg[15:0], in};
end
end
end
assign out_bytes = out_reg;
assign done = (state == DONE);
endmodule

