module top_module (
input clk,
input in,
input reset,
output done
);
parameter IDLE = 2'b00;
parameter START_BIT = 2'b01;
parameter DATA_BITS = 2'b10;
parameter STOP_BIT = 2'b11;
reg [1:0] state;
reg [7:0] data;
reg stop_bit_detected;
initial begin
state = IDLE;
data = 8'b0;
stop_bit_detected = 0;
end
always @(posedge clk or posedge reset) begin
if (reset) begin
state <= IDLE;
data <= 8'b0;
stop_bit_detected <= 0;
end else begin
case (state)
IDLE: begin
if (in == 1'b0) begin
state <= START_BIT;
data <= 8'b0;
stop_bit_detected <= 0;
end
end
START_BIT: begin
state <= DATA_BITS;
data <= {data[6:0], in};
stop_bit_detected <= 0;
end
DATA_BITS: begin
state <= DATA_BITS;
data <= {data[6:0], in};
stop_bit_detected <= 0;
if (data[7] == 1'b1) begin
state <= STOP_BIT;
end
end
STOP_BIT: begin
if (in == 1'b1) begin
state <= IDLE;
stop_bit_detected <= 1;
end
end
endcase
end
end
assign done = stop_bit_detected;
endmodule

