module top_module (
input clk,
input in,
input reset,
output [7:0] out_byte,
output done
);
reg [2:0] state;
reg [7:0] data_byte;
reg start_bit;
reg stop_bit;
reg [2:0] next_state;
always @(posedge clk or posedge reset) begin
if (reset) begin
state <= 0;
data_byte <= 0;
start_bit <= 0;
stop_bit <= 0;
end else begin
state <= next_state;
data_byte <= in ? {data_byte[6:0], in} : data_byte;
start_bit <= in;
stop_bit <= data_byte[7];
end
end
always @(*) begin
case (state)
0: next_state = start_bit ? 1 : 0;
1: next_state = 2;
2: next_state = 3;
3: next_state = stop_bit ? 0 : 2;
endcase
end
assign out_byte = data_byte;
assign done = (state == 3);
endmodule
endmodule

