module top_module (
input clk,
input areset,
input bump_left,
input bump_right,
input ground,
output reg walk_left,
output reg walk_right,
output reg aaah
);
parameter WALK_LEFT = 2'b00;
parameter WALK_RIGHT = 2'b01;
parameter FALL = 2'b10;
reg [1:0] state;
always @(posedge clk or posedge areset) begin
if (areset) begin
state <= WALK_LEFT;
end else begin
case (state)
WALK_LEFT: begin
if (bump_right) begin
state <= WALK_RIGHT;
end else if (ground == 0) begin
state <= FALL;
end
end
WALK_RIGHT: begin
if (bump_left) begin
state <= WALK_LEFT;
end else if (ground == 0) begin
state <= FALL;
end
end
FALL: begin
if (ground) begin
if (state == WALK_LEFT) begin
state <= WALK_LEFT;
end else if (state == WALK_RIGHT) begin
state <= WALK_RIGHT;
end
end
end
endcase
end
end
always @(state) begin
case (state)
WALK_LEFT: begin
walk_left <= 1;
walk_right <= 0;
aaah <= 0;
end
WALK_RIGHT: begin
walk_left <= 0;
walk_right <= 1;
aaah <= 0;
end
FALL: begin
walk_left <= 0;
walk_right <= 0;
aaah <= 1;
end
endcase
end
endmodule

canonical_solution:
	parameter WL=0, WR=1, FALLL=2, FALLR=3;
	reg [1:0] state;
	reg [1:0] next;

    always_comb begin
		case (state)
			WL: next = ground ? (bump_left ? WR : WL) : FALLL;
			WR: next = ground ? (bump_right ? WL: WR) : FALLR;
			FALLL: next = ground ? WL : FALLL;
			FALLR: next = ground ? WR : FALLR;
		endcase
    end

    always @(posedge clk, posedge areset) begin
		if (areset) state <= WL;
        else state <= next;
	end

	assign walk_left = (state==WL);
	assign walk_right = (state==WR);
	assign aaah = (state == FALLL) || (state == FALLR);

endmodule

