module top_module (
input clk,
input areset,
input bump_left,
input bump_right,
input ground,
input dig,
output walk_left,
output walk_right,
output aaah,
output digging
);
parameter WALK_LEFT = 2'b00;
parameter WALK_RIGHT = 2'b01;
parameter FALL = 2'b10;
parameter DIG = 2'b11;
reg [1:0] state_reg;
reg [1:0] state_next;
always @(posedge clk or posedge areset) begin
if (areset) begin
state_reg <= WALK_LEFT;
end else begin
state_reg <= state_next;
end
end
assign walk_left = (state_reg == WALK_LEFT);
assign walk_right = (state_reg == WALK_RIGHT);
assign aaah = (state_reg == FALL);
assign digging = (state_reg == DIG);
always @* begin
case (state_reg)
WALK_LEFT: begin
if (bump_right) begin
state_next = WALK_RIGHT;
end else if (bump_left) begin
state_next = WALK_LEFT;
end else if (!ground) begin
state_next = FALL;
end else if (dig) begin
state_next = DIG;
end else begin
state_next = WALK_LEFT;
end
end
WALK_RIGHT: begin
if (bump_left) begin
state_next = WALK_LEFT;
end else if (bump_right) begin
state_next = WALK_RIGHT;
end else if (!ground) begin
state_next = FALL;
end else if (dig) begin
state_next = DIG;
end else begin
state_next = WALK_RIGHT;
end
end
FALL: begin
if (ground) begin
state_next = state_reg;
end else begin
state_next = FALL;
end
end
DIG: begin
if (!ground) begin
state_next = FALL;
end else if (dig) begin
state_next = DIG;
end else begin
state_next = state_reg;
end
end
endcase
end
endmodule

