reg [4:0] lfsr;
always @(posedge clk or posedge reset) begin
if (reset) begin
lfsr <= 5'b1;
end else begin
lfsr <= {lfsr[3] ^ lfsr[4], lfsr[0]};
end
end
assign q = lfsr;
endmodule

