module top_module (
input [3:0] x,
input [3:0] y,
output [4:0] sum
);
wire [3:0] carry;
wire [3:0] sum1;
wire [3:0] carry1;
assign sum1[0] = x[0] ^ y[0];
assign carry1[0] = x[0] & y[0];
wire [3:0] sum2;
wire [3:0] carry2;
assign sum2[1] = x[1] ^ y[1] ^ carry1[0];
assign carry2[0] = (x[1] & y[1]) | (carry1[0] & (x[1] ^ y[1]));
wire [3:0] sum3;
wire [3:0] carry3;
assign sum3[2] = x[2] ^ y[2] ^ carry2[0];
assign carry3[0] = (x[2] & y[2]) | (carry2[0] & (x[2] ^ y[2]));
wire [3:0] sum4;
wire [3:0] carry4;
assign sum4[3] = x[3] ^ y[3] ^ carry3[0];
assign carry4[0] = (x[3] & y[3]) | (carry3[0] & (x[3] ^ y[3]));
assign sum = {carry4[0], sum4[3:0]};
endmodule

