input clk,
input reset,
input w,
output z
);
parameter A = 0;
parameter B = 1;
parameter C = 2;
parameter D = 3;
parameter E = 4;
parameter F = 5;
reg [2:0] state;
always @(posedge clk) begin
if (reset) begin
state <= A;
end else begin
case (state)
A: begin
if (w) begin
state <= A;
end else begin
state <= B;
end
end
B: begin
if (w) begin
state <= D;
end else begin
state <= C;
end
end
C: begin
if (w) begin
state <= D;
end else begin
state <= E;
end
end
D: begin
if (w) begin
state <= A;
end else begin
state <= F;
end
end
E: begin
if (w) begin
state <= D;
end else begin
state <= E;
end
end
F: begin
if (w) begin
state <= D;
end else begin
state <= C;
end
end
endcase
end
end
always @(posedge clk) begin
if (state == E || state == F) begin
z <= 1'b1;
end else begin
z <= 1'b0;
end
end
endmodule
endmodule

prompt:
module top_module (
	input clk,
	input reset,
	input w,
	output z
);

canonical_solution:
	parameter A=0, B=1, C=2, D=3, E=4, F=5;
	reg [2:0] state, next;

	always @(posedge clk)
		if (reset)
			state <= A;
		else 
			state <= next;


	always_comb begin
		case(state)
			A: next = w ? A : B;
			B: next = w ? D : C;
			C: next = w ? D : E;
			D: next = w ? A : F;
			E: next = w ? D : E;
			F: next = w ? D : C;
			default: next = 'x;
		endcase
	end

	assign z = (state == E || state == F);

endmodule

