module top_module(
input clk,
input reset,
input data,
output reg shift_ena,
output reg counting,
input done_counting,
output reg done,
input ack );
typedef enum logic [2:0] {
IDLE,
START_DETECT,
SHIFT_IN,
WAIT_COUNTING,
NOTIFY,
WAIT_ACK
} state_t;
reg state_t current_state = IDLE;
reg state_t next_state;
parameter START_SEQ = 4'b1101;
reg [3:0] shift_ena_counter;
always @(posedge clk or posedge reset) begin
if (reset) begin
current_state <= IDLE;
shift_ena_counter <= 0;
end else begin
current_state <= next_state;
shift_ena_counter <= shift_ena_counter + 1;
end
end
always @* begin
next_state = current_state;
shift_ena = 0;
counting = 0;
done = 0;
case (current_state)
IDLE: begin
if (data == START_SEQ) begin
next_state = START_DETECT;
end
end
START_DETECT: begin
shift_ena = 1;
next_state = SHIFT_IN;
end
SHIFT_IN: begin
if (shift_ena_counter == 4) begin
counting = 1;
next_state = WAIT_COUNTING;
end
end
WAIT_COUNTING: begin
if (done_counting) begin
done = 1;
next_state = NOTIFY;
end
end
NOTIFY: begin
if (ack) begin
next_state = WAIT_ACK;
end
end
WAIT_ACK: begin
if (!ack) begin
next_state = IDLE;
end
end
endcase
end
endmodule

canonical_solution:
	typedef enum logic[3:0] {
		S, S1, S11, S110, B0, B1, B2, B3, Count, Wait
	} States;

	States state, next;

	always_comb begin
		case (state)
			S: next = States'(data ? S1: S);
			S1: next = States'(data ? S11: S);
			S11: next = States'(data ? S11 : S110);
			S110: next = States'(data ? B0 : S);
			B0: next = B1;
			B1: next = B2;
			B2: next = B3;
			B3: next = Count;
			Count: next = States'(done_counting ? Wait : Count);
			Wait: next = States'(ack ? S : Wait);
			default: next = States'(4'bx);
		endcase
	end

	always @(posedge clk) begin
		if (reset) state <= S;
		else state <= next;
	end

	always_comb begin
		shift_ena = 0; counting = 0; done = 0;
		if (state == B0 || state == B1 || state == B2 || state == B3)
			shift_ena = 1;
		if (state == Count)
			counting = 1;
		if (state == Wait)
			done = 1;

		if (|state === 1'bx) begin
			{shift_ena, counting, done} = 'x;
		end

	end


endmodule

