module top_module(
input clk,
input reset,
input data,
output start_shifting);
reg [3:0] state;
reg start_shifting_reg;
always @(posedge clk or posedge reset) begin
if (reset) begin
state <= 4'b0000;
start_shifting_reg <= 0;
end else begin
case (state)
4'b0000: begin
if (data) begin
state <= 4'b0001;
end else begin
state <= 4'b0000;
end
end
4'b0001: begin
if (data) begin
state <= 4'b0010;
end else begin
state <= 4'b0000;
end
end
4'b0010: begin
if (data) begin
state <= 4'b0011;
end else begin
state <= 4'b0000;
end
end
4'b0011: begin
if (data) begin
state <= 4'b0011;
end else begin
state <= 4'b0100;
end
end
4'b0100: begin
if (data) begin
state <= 4'b0010;
end else begin
state <= 4'b0000;
end
end
endcase
if (state == 4'b0100) begin
start_shifting_reg <= 1;
end else begin
start_shifting_reg <= 0;
end
end
end
assign start_shifting = start_shifting_reg;
endmodule

