module top_module(
input mode,
input too_cold,
input too_hot,
input fan_on,
output heater,
output aircon,
output fan
);
wire fan_out;
wire heater_out;
wire aircon_out;
assign fan_out = (fan_on || too_cold || too_hot);
assign heater_out = (mode && too_cold);
assign aircon_out = (!mode && too_hot);
assign fan = fan_out;
assign heater = heater_out;
assign aircon = aircon_out;
endmodule

canonical_solution:

	assign fan = (mode ? too_cold : too_hot) | fan_on;
	assign heater = (mode & too_cold);
	assign aircon = (~mode & too_hot);

endmodule

