        data_output <= in;
        valid_output <= 0;
        state <= 3;
      end
      3: begin // done state
        counter <= 0;
        data_output <= in;
        valid_output <= 1;
        state <= 0;
      end
    endcase
  end

  assign out = data_output;

endmodule

